<?phpnamespace App\Http\Controllers;use App\Autor;use Illuminate\Http\Request;use DB;use Carbon\Carbon;class AutoresController extends Controller{    public function __construct(){    }    /**     * Display a listing of the resource.     *     * @return \Illuminate\Http\Response     */    public function index(){        $autores = DB::table('autores')->get();        Autor::all();        return view('autores.index', compact('autores'));    }    /**     * Show the form for creating a new resource.     *     * @return \Illuminate\Http\Response     */    public function create(){        return view('autores.create');    }    /**     * Store a newly created resource in storage.     *     * @param  \Illuminate\Http\Request  $request     * @return \Illuminate\Http\Response     */    public function store(Request $request){        Autor::create($request->all());        return back()            ->with('info', 'Autor creado correctamente.');    }    /**     * Display the specified resource.     *     * @param  int  $id     * @return \Illuminate\Http\Response     */    public function show($id){        $autor = Autor::findOrFail($id);        return view('autores.show', compact('autor'));    }    /**     * Show the form for editing the specified resource.     *     * @param  int  $id     * @return \Illuminate\Http\Response     */    public function edit($id){        $autor = Autor::findOrFail($id);        return view('autores.edit', compact('autor'));    }    /**     * Update the specified resource in storage.     *     * @param  \Illuminate\Http\Request  $request     * @param  int  $id     * @return \Illuminate\Http\Response     */    public function update(Request $request, $id){        Autor::findOrFail($id)->update($request->all());        return redirect()->route('autores.index');    }    /**     * Remove the specified resource from storage.     *     * @param  int  $id     * @return \Illuminate\Http\Response     */    public function destroy($id){        Autor::findOrFail($id)->delete();        return redirect()->route('autores.index');    }}