<?phpnamespace App\Http\Controllers;use App\Autor;use DB;use App\Http\Requests\CreateAutoresRequest;class AutoresController extends Controller{    public function index(){        $autores = DB::table('autores')->get();        Autor::all();        return view('autores.index', compact('autores'));    }    public function create(){        return view('autores.create');    }    public function store(CreateAutoresRequest $request){        Autor::create($request->all());        return redirect()->route('autores.index')->with('info', 'Autor creado correctamente.');    }    public function show($id){        $autor = Autor::findOrFail($id);        return view('autores.show', compact('autor'));    }    public function edit($id){        $autor = Autor::findOrFail($id);        return view('autores.edit', compact('autor'));    }    public function update(CreateAutoresRequest $request, $id){        Autor::findOrFail($id)->update($request->all());        return redirect()->route('autores.index')->with('info', 'Autor modificado correctamente.');    }    public function destroy($id){        Autor::findOrFail($id)->delete();        return redirect()->route('autores.index')->with('info', 'Autor eliminado correctamente.');    }}