<?phpnamespace App\Http\Controllers;use App\Usuario;use App\Http\Requests\CreateUsuarioRequest;use DB;class UsuariosController extends Controller{    public function __construct(){    }    public function index(){        $usuarios = DB::table('usuarios')->get();        Usuario::all();        return view('usuarios.index', compact('usuarios'));    }    public function create(){        $categorias = DB::table('categorias')->where('id_grupo', '3')->get();        return view('usuarios.create', compact('categorias'));    }    public function store(CreateUsuarioRequest $request){        Usuario::create($request->all());        return redirect()->route('usuarios.index')->with('info', 'Usuario creado correctamente.');    }    public function show($id){        $usuario = Usuario::findOrFail($id);        return view('usuarios.show', compact('usuario'));    }    public function edit($id){        $usuario = Usuario::findOrFail($id);        $categorias = Categoria::pluck('nombre', 'id');        return view('usuarios.edit', compact('usuario', 'categorias'));    }    public function update(CreateUsuarioRequest $request, $id){        Usuario::findOrFail($id)->update($request->all());        return redirect()->route('usuarios.index')->with('info', 'Usuario modificado correctamente.');    }    public function destroy($id){        Usuario::findOrFail($id)->delete();        return redirect()->route('usuarios.index');    }}