<?phpnamespace App\Http\Controllers;use App\Usuario;use Illuminate\Http\Request;use DB;use Carbon\Carbon;class UsuariosController extends Controller{    public function __construct(){    }    public function index(){        $usuarios = DB::table('usuarios')->get();        Usuario::all();        return view('usuarios.index', compact('usuarios'));    }    public function create(){        return view('usuarios.create');    }    public function store(Request $request){        Usuario::create($request->all());        return back()->with('info', 'Usuario creado correctamente');    }    public function show($id){        $usuario = Usuario::findOrFail($id);        return view('usuarios.show', compact('usuario'));    }    public function edit($id){        $usuario = Usuario::findOrFail($id);        return view('usuarios.edit', compact('usuario'));    }    public function update(Request $request, $id){        Usuario::findOrFail($id)->update($request->all());        return redirect()->route('usuarios.index')->with('info', 'Usuario modificado correctamente.');    }    public function destroy($id){        Usuario::findOrFail($id)->delete();        return redirect()->route('usuarios.index');    }}